mapper.select:
SELECT COUNT(*)
FROM HR
WHERE EMAIL = #{email}

mapper.insert:
INSERT INTO HR 
(
      EMP_NO
    , DEP_NAME
    , DEP_NO
    , ADMIN_LEVEL
    , EMP_RANK
    , EMP_POSITION
    , EMP_NAME
    , EMAIL
    , EMP_PWD
    , PHONE
    , RESI_NO
    , ADDRESS
    , BANK
    , BANK_ACC
    , EMP_INFO
    , ENTRY_DATE
    , RESIGN_YN
    , CHANGE_NAME
)
VALUES
(
      #{empNo}
    , #{depName}
    , #{depNo}
    , #{adminLevel}
    , #{empRank}
    , #{empPosition}
    , #{empName}
    , #{email}
    , #{empPwd}
    , #{phone}
    , #{resiNo}
    , #{address}
    , #{bank}
    , #{bankAcc}
    , #{empInfo}
    , #{entryDate}
    , 'N'
    , #{changeName}
)

INSERT INTO HR_PROFILE
(
	  FILE_NO
	, EMP_NO
	, CHANGE_NAME
)
VALUES
(
	  HR_PROFILE_SEQ.NEXTVAL
	, #{empNo}
	, #{changeName}
)
